import React, { useEffect, useRef, useState } from 'react'
import Card from './Card'

export type CardType = {
  id: number,
  color: string,
  state: 'flipped' | 'matched' | ''
}

const colors: CardType[] = [
  { id: 1, color: 'green', state: '' },
  { id: 1, color: 'green', state: '' },
  { id: 2, color: 'yellow', state: '' },
  { id: 2, color: 'yellow', state: '' },
  { id: 3, color: 'red', state: '' },
  { id: 3, color: 'red', state: '' },
  { id: 4, color: 'orange', state: '' },
  { id: 4, color: 'orange', state: '' },
  { id: 5, color: 'purple', state: '' },
  { id: 5, color: 'purple', state: '' },
  { id: 6, color: 'pink', state: '' },
  { id: 6, color: 'pink', state: '' },
  { id: 7, color: 'brown', state: '' },
  { id: 7, color: 'brown', state: '' },
  { id: 8, color: 'blue', state: '' },
  { id: 8, color: 'blue', state: '' },
]

const shuffledCards = (): CardType[] => {
  return JSON.parse(JSON.stringify(colors)).sort(() => Math.random() - 0.5)
}

const deepshuffledCards = JSON.parse(JSON.stringify(colors))
// };

const Cards = () => {
  const [cards, setCards] = useState<CardType[]>(shuffledCards);
  const [flippedCards, setFlippedCards] = useState<number[]>([]);
  const [score, setScore] = useState(0);
  const timeout = useRef(null);


  const resetGame = () => {
    setCards(shuffledCards)
    setFlippedCards([])
    setScore(0)
  }

  useEffect(() => {
    resetGame()
  },[])

  useEffect(() => {
    if (flippedCards.length === 2) {
      const [firstIndex, secondIndex] = flippedCards;
      const areCardsEqual = cards[firstIndex].id === cards[secondIndex].id;

      setTimeout(() => {
        const newCards = [...cards];

        if (areCardsEqual) {
          setScore((prevScore) => prevScore + 1);
          newCards[firstIndex].state = "matched";
          newCards[secondIndex].state = "matched";
        } else {
          newCards[firstIndex].state = "";
          newCards[secondIndex].state = "";
          setScore((prevScore) => prevScore -1);
        }

        setCards(newCards);
        setFlippedCards([]);
      }, 2000);
    }
  }, [flippedCards, cards]);

  console.log('flippedcards', flippedCards)

  const handleCardClick = (index: number) => {
    if (cards[index].state === "matched") return

    if (flippedCards.length < 2 && cards[index].state !== "flipped") {
      setFlippedCards((prevCards) => [...prevCards, index]);
      setCards((prevCards) => {
        const newCards = [...prevCards];
        newCards[index].state = "flipped";
        return newCards;
      });
    }
  };

  const isGameOver = cards.every((card) => card.state === "matched");

  useEffect(() => {
    if (isGameOver) {
      resetGame()
    }
  }, [isGameOver, score]);

  return(
    <>
      <div className="score">
        <button className="new-game-button" onClick={resetGame}>New Game</button>
        <p>Score: {score}</p>
      </div>

      <div className='container'>
        {cards.map((card, index) =>
          <Card card={card} key={index} index={index} onClick={handleCardClick} />
        )}
      </div>
    </>
  )
}

export default Cards
