import React from 'react'
import draftit from '../assets/images/draftit.png'
import { CardType } from '../types/CardType'
import styled from 'styled-components'

interface Props {
  card: CardType
  index: number
  onClick: (id: number) => void
}

const Card: React.FC<Props> = ({ card, index, onClick}) => {

  return(
    <CardContainer className={`card ${card.state}`} onClick={() => onClick(index)}>
      {card.state === "flipped" && (
        <FrontCard color={card.color}></FrontCard>
      )}
      <BackCard>
        <img src={draftit} alt='draftit img' />
      </BackCard>
    </CardContainer>
  )
}

const CardContainer = styled.div`
  display: flex;
  height: 175px;
  width: 125px;
  position: relative;
  border-radius: 6px;
`
const CardStyle = styled.div`
  width: 100%;
  height: 100%;
  backface-visibility: hidden;
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  transform-style: preserve-3d;
  transition: transform 0.2s;
`
const BackCard = styled(CardStyle)`
  background-color: #f5f5dc;
  transform: rotateY(0);
`

const FrontCard = styled(CardStyle)<{color: string}>`
  background: ${(p => p.color)};
`

export default Card
