import React from 'react'
import { CardType } from './Cards'
import draftit from '../assets/images/draftit.png'

interface Props {
  card: CardType
  onClick: (id: number) => void
}

const Card: React.FC<Props> = ({ card, onClick}) => {


  return(
    <div className='card' onClick={() => onClick(card.id)}>
        <div className='front' style={{ background: card.state === 'flipped' ? card.color : '#f5f5dc;'}} />
        {/* <img className='back' src={draftit} alt='draftit img' /> */}
    </div>
)
}

export default Card
