import React, { useRef, useState } from 'react'
import Card from './Card'

export type CardType = {
  id: number,
  color: string,
  state: string,
}

const colors: CardType[] = [
  { id: 1, color: 'green', state: 'flipped' },
  { id: 1, color: 'green', state: 'flipped' },
  { id: 2, color: 'yellow', state: 'unflipped' },
  { id: 2, color: 'yellow', state: 'flipped' },
  { id: 3, color: 'red', state: 'funlipped' },
  { id: 3, color: 'red', state: 'flipped' },
  { id: 4, color: 'orange', state: 'flipped' },
  { id: 4, color: 'orange', state: 'flipped' },
  { id: 5, color: 'purple', state: 'unflipped' },
  { id: 5, color: 'purple', state: 'flipped' },
  { id: 6, color: 'pink', state: 'flipped' },
  { id: 6, color: 'pink', state: 'flipped' },
  { id: 7, color: 'brown', state: 'unlipped' },
  { id: 7, color: 'brown', state: 'unflipped' },
  { id: 8, color: 'blue', state: 'unflipped' },
  { id: 8, color: 'blue', state: 'flipped' },
]

const Cards = () => {
  const [cards, setCards] = useState<Array<CardType>>([])
  const [score, setScore] = useState<number>(0)
  const disabled = useRef(false)
  const prevIndex = useRef(-1)
  const [flippedIndices, setFlippedIndices] = useState<number[]>([]);


  const generatRandomColors = () => {
    const shuffledCards = colors.sort(() => Math.random() - 0.5)
    setCards(shuffledCards)
    setScore(0)
  }

  const handleCardClick = (id: number) => {
    if(disabled.current) return
    console.log('clicked card', id)
  }

  return(
    <>
      <button className="new-game" onClick={generatRandomColors}>New Game</button>
      <div className='container'>
        {cards.map((card, index) =>
          <Card card={card} key={index} onClick={handleCardClick} />
        )}
      </div>
    </>
  )
}

export default Cards
