import React, { useRef, useState } from 'react'
import Card from './Card'
import { shuffledCards } from '../utils/ShuffleArray';
import { CardType } from '../types/CardType';
import Overlay from './Overlay';
import styled from 'styled-components'

const Cards = () => {
  const [cards, setCards] = useState<CardType[]>(shuffledCards);
  const [score, setScore] = useState(0);
  const [disabled, setDisabled] = useState(false)
  const prevIndex = useRef(-1)

  const resetGame = () => {
    setCards(shuffledCards)
    prevIndex.current = -1
    setScore(0)
    setDisabled(false)
  }

  const handleCardClick = async (index: number) => {
    const currCard = cards[index];
    const prevCard = cards[prevIndex.current];

    if (currCard.state === 'matched' || disabled) return;

    updateStatus([currCard], 'flipped');

    if (!prevCard || prevIndex.current === index) {
      prevIndex.current = index;
      return;
    }

      setDisabled(true)
      setTimeout(() => {
      if (currCard.id === prevCard.id) {
        updateStatus([currCard, prevCard], 'matched');
        setScore((prevScore) => prevScore + 1);
        setDisabled(false)
      } else {
        updateStatus([currCard, prevCard], '')
        setScore((prevScore) => prevScore - 1);
        setDisabled(false)
      }
    }, 2000)

      prevIndex.current = -1;
  };

  const updateStatus = (cards1: CardType[], state: 'flipped' | 'matched' | '') =>{
      cards1.forEach((card) => (card.state = state))
      setCards([...cards])
  }

  const isGameOver = cards.every((card) => card.state === 'matched');

  return(
    <GameWrapper>
      <div className="score">
        <button onClick={resetGame}>New Game</button>
        <h1>Score: {score}</h1>
      </div>

      <div className='container'>
        {cards.map((card, index) =>
          <Card card={card} key={index} index={index} onClick={handleCardClick} />
        )}
      </div>
      {isGameOver && (
        <Overlay onClick={resetGame} score={score} />
      )}
    </GameWrapper>
  )
}

const GameWrapper = styled.div`
  display: flex;
`
// const GameWrapper = styled.div`
//   display: flex;
// `

export default Cards
