import React, { useEffect, useRef, useState } from 'react'
import Card from './Card'
import { shuffledCards } from '../utils/ShuffleArray';
import { CardType } from '../types/CardType';

const Cards = () => {
  const [cards, setCards] = useState<CardType[]>(shuffledCards);
  //const [flippedCards, setFlippedCards] = useState<number[]>([]);
  const [score, setScore] = useState(0);
  const prevIndex = useRef(-1)


  const resetGame = () => {
    setCards(shuffledCards)
    prevIndex.current = -1
    setScore(0)
  }

  // useEffect(() => {
  //   if (flippedCards.length === 2) {
  //     const [firstIndex, secondIndex] = flippedCards;
  //     const areCardsEqual = cards[firstIndex].id === cards[secondIndex].id;

  //     setTimeout(() => {
  //       const newCards = [...cards];

  //       if (areCardsEqual) {
  //         setScore((prevScore) => prevScore + 1);
  //         newCards[firstIndex].state = "matched";
  //         newCards[secondIndex].state = "matched";
  //       } else {
  //         newCards[firstIndex].state = "";
  //         newCards[secondIndex].state = "";
  //         setScore((prevScore) => prevScore -1);
  //       }

  //       setCards(newCards);
  //       setFlippedCards([]);
  //     }, 2000);
  //   }
  // }, [flippedCards, cards]);

  // const handleCardClick = (index: number) => {
  //   if (cards[index].state === "matched") return

  //   if (flippedCards.length < 2 && cards[index].state !== "flipped") {
  //     setFlippedCards((prevCards) => [...prevCards, index]);
  //     setCards((prevCards) => {
  //       const newCards = [...prevCards];
  //       newCards[index].state = "flipped";
  //       return newCards;
  //     });
  //   }
  // };

//   const updateStatus = (index: number) =>{
//     const currCard = cards[index]
//     const prevCard = cards[prevIndex.current]
//     console.log('current', cards[index])
//     console.log('prev', cards[prevIndex.current])
//     setTimeout(() => {
//       const newCards = [...cards];
//       if(currCard.id === prevCard.id){
//         setScore((prevScore) => prevScore + 1);
//         newCards[index].state = "matched"
//         newCards[prevIndex.current].state = "matched"
//       }else{
//         newCards[index].state = ""
//         newCards[prevIndex.current].state = ""
//         setScore((prevScore) => prevScore -1);
//       }

//       setCards(newCards);
//       }, 2000);
//       prevIndex.current = -1
// }

  const handleCardClick = (index: number) => {
    const currCard = cards[index]
    const prevCard = cards[prevIndex.current]

    if (currCard.state === "matched") return


    if (!prevCard || prevIndex.current === index) {
      prevIndex.current = index
      return
    }
    setTimeout(() => {
      updateStatus([currCard], "flipped")

      if (currCard.id === prevCard.id) {
        updateStatus([currCard, prevCard], "matched")
        setScore((prevScore) => prevScore + 1)
      }
      else {
        updateStatus([currCard, prevCard], "")
        setScore((prevScore) => prevScore -1)
      }
    }, 2000);


    prevIndex.current = -1
  }

  const updateStatus = (cards1: CardType[], state: 'flipped' | 'matched' | '') =>{
      cards1.forEach((card) => (card.state = state))
      setCards([...cards])
  }

  const isGameOver = cards.every((card) => card.state === "matched");

  return(
    <div className="game-wrapper">
      <div className="score">
        <button onClick={resetGame}>New Game</button>
        <h1>Score: {score}</h1>
      </div>

      <div className='container'>
        {cards.map((card, index) =>
          <Card card={card} key={index} index={index} onClick={handleCardClick} />
        )}
      </div>
      {isGameOver && (
        <div className="overlay">
          <div className="overlay-content">
            <p>Game over! Your score is {score}.</p>
            <button onClick={resetGame}>Play Again</button>
          </div>
        </div>
      )}
    </div>
  )
}

export default Cards
