import styled from 'styled-components'

interface Props {
  onClick: () => void
  score: number
}

const Overlay: React.FC<Props> = ({onClick, score}) => {
  const isNegativeScore= score < 0

  return (
    <OverlayWrapper>
      <ContentWrapper won={!isNegativeScore}>
        <Text won={!isNegativeScore}>Game over! Your score is {score}.</Text>
        <Button onClick={onClick}>Play Again</Button>
      </ContentWrapper>
  </OverlayWrapper>
  )
}

const OverlayWrapper = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: #0808087d;
  display: flex;
  justify-content: center;
  align-items: center;
`
const ContentWrapper = styled.div<{won: boolean}>`
  background: ${(p) => p.won ? '#ffffff' : '#ffe8e7'};
  padding: 50px;
  border-radius: 5px;
  text-align: center;
`
const Text = styled.div<{won: boolean}>`
  font-size: 18px;
  margin-bottom: 10px;
  color: ${(p) => p.won ? '#282c34' : '#f1807e'};
`
const Button = styled.button`
  padding: 10px 20px;
  background-color: #4caf50;
  color: #ffffff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
`
export default Overlay
