import React from 'react'
import draftit from '../assets/images/draftit.png'
import { CardType } from '../types/CardType'
import styled from 'styled-components'

interface Props {
  card: CardType
  index: number
  onClick: (id: number) => void
}

const Card: React.FC<Props> = ({ card, index, onClick}) => {

  return(
    <div className={`card ${card.state}`} onClick={() => onClick(index)}>
      {card.state === "flipped" && (
        <div className="front" style={{ backgroundColor: card.color }}></div>
      )}
      <div className='back'>
        <img src={draftit} alt='draftit img' />
      </div>
    </div>
  )
}

const CardContainer = styled.div`
  display: flex;
  height: 175px;
  width: 125px;
  position: relative;
  border-radius: 6px;
`

export default Card
