import styled, { createGlobalStyle } from "styled-components";


export const GlobalStyle = createGlobalStyle`
  .App {
    text-align: center;
    margin: 0;
    padding: 0;
  }

  body {
    background-color: #282c34;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #ffffff;
  }
`;

export const button = styled.button`
  padding: 10px 20px;
  background-color: #4caf50;
  color: #ffffff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
`
